import {React, useRef, useState} from 'react';
import {Outlet,NavLink} from 'react-router-dom'
import PageProfile from '../Images/PageProfile.svg'
import { FaUser, FaSearch } from "react-icons/fa";

// style={{width:"100%"}}
function MainPage(){
    const searchBtn = useRef();

    const nav0 = useRef();
    const nav1 = useRef();
    const nav2 = useRef();

    const searchSubmint = () =>{
        console.log(searchBtn.current.value)
    }

    const [isOpen, setIsOpen] = useState(false);

    const toggleNavbar = () => {
        setIsOpen(!isOpen);
    };
    return(
        <div>
            <header className='bg-primary p-2 d-flex align-items-center text-light'>
                <img alt="profile" className='border border-2 rounded-circle' src={PageProfile} style={{width:"100px"}}/>
                <div className='m-2 page-titulo'>
                    <h1>Pagina de Prueba</h1>
                    <h3>encabezado de la pagina</h3>
                </div>
                <div className='ms-2 me-2' style={{width:"100%"}}>
                    <nav className="navbar navbar-expand-lg bg-primary">
                        <div className="container-fluid">
                            <button 
                                className="navbar-toggler " 
                                type="button" 
                                data-bs-toggle="collapse" 
                                data-bs-target="#navbarNavAltMarkup" 
                                aria-controls="navbarNavAltMarkup" 
                                aria-expanded="false" 
                                aria-label="Toggle navigation"
                                onClick={toggleNavbar}
                                >
                                <span className="navbar-toggler-icon"></span>
                            </button>
                            <div 
                                className={`collapse navbar-collapse mt-2 fs-4 ${isOpen ? 'show' : ''}`} 
                                id="navbarNavAltMarkup"
                            >
                                <form onSubmit={ev=>{
                                    ev.preventDefault();
                                    searchSubmint();
                                }} className='input-group' style={{height:"100%"}}>
                                    <input ref={searchBtn} className='form-control' type='text'/>
                                    <span onClick={
                                        ev=>{
                                            ev.preventDefault();
                                            searchSubmint();
                                        }
                                    } className="input-group-text" role="button" id="basic-addon2"><FaSearch /></span>
                                </form>
                                <div className="
                                    navbar-nav 
                                    align-items-center 
                                    justify-content-end 
                                    bg-primary
                                " 
                                style={{width:"100%"}}>
                                    <NavLink className="text-black link-light nav-link me-1 ms-1" href="#">Limpiar</NavLink>
                                    <NavLink className="text-black link-light nav-link me-1 ms-1" href="#">Usuarios</NavLink>
                                    <NavLink className="text-black link-light nav-link me-1 ms-1" href="#">Salas</NavLink>
                                    <NavLink className='text-black link-light nav-link me-1 ms-1 d-flex'>Login <FaUser className='mt-2 ms-2' size={20}/></NavLink>
                                </div>
                            </div>
                        </div>
                    </nav>
                    {/*
                        <div role="button" className='text-black link-light d-flex justify-content-end align-items-center me-1 ms-1'>                
                    </div>*/}
                </div>

            </header>
            <div>
                <h3>esto es el cuerpo de la pagina</h3>
            </div>
            <div>
                <h3>esto es el pie de la pagina</h3>
            </div>
            <Outlet/>
        </div>
    )
}

export default MainPage;
